try {\n       $mysqli->query("INSERT INTO tblUser (email, ...) VALUES ('foo@bar.org', ...)");\n   } catch (mysqli_sql_exception $e) {\n       if ($e->getCode() == 1062) {\n           // Handle duplicate entry error (Error code 1062 is for duplicate entry)\n           echo "Duplicate entry for email: foo@bar.org";\n       } else {\n           // Handle other errors\n           echo "An error occurred: " . $e->getMessage();\n       }\n   }