<?php\n$html = '<span lang="en">English Content</span><span lang="fr">French content</span>';\n\n// Function to extract content by lang attribute\nfunction extractSpanContent($html, $lang) {\n    $pattern = '/<span lang="' . preg_quote($lang, '/') . '">(.*?)<\/span>/';\n    preg_match($pattern, $html, $matches);\n    return $matches[1] ?? null;\n}\n\n$lang = 'fr';\n$content = extractSpanContent($html, $lang);\n\nif ($content !== null) {\n    echo "Content for lang '$lang': $content\n";\n} else {\n    echo "No content found for lang '$lang'\n";\n}\n?>