<?php\n    // Define the target directory\n    $targetDir = "uploads/";\n\n    // Check if the target directory exists, if not create it\n    if (!is_dir($targetDir)) {\n        mkdir($targetDir, 0755, true);\n    }\n\n    if ($_FILES['Filedata']['size'] >= 520000) {\n        echo "Sorry, not moved. Size must be below 520000 bytes.";\n        return;\n    }\n\n    // Get file extension\n    $ext = strtolower(pathinfo($_FILES['Filedata']['name'], PATHINFO_EXTENSION));\n\n    // Sanitize file name\n    $fileName = preg_replace("/[^a-zA-Z0-9._-]/", "", basename($_FILES['Filedata']['name']));\n    $targetFilePath = $targetDir . $fileName;\n\n    if (move_uploaded_file($_FILES['Filedata']['tmp_name'], $targetFilePath)) {\n        echo "File moved successfully.";\n    } else {\n        echo "Sorry, there was an error moving the file.";\n    }\n?>