<?php\n// Database connection\n$servername = "localhost";\n$username = "username";\n$password = "password";\n$dbname = "database";\n\n// Create connection\n$conn = new mysqli($servername, $username, $password, $dbname);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Get the div ID from the POST request\n$divId = $_POST['divId'];\n\n// Prepare and execute the query to fetch the text based on the div ID\n$sql = $conn->prepare("SELECT text FROM div_texts WHERE div_id = ?");\n$sql->bind_param("s", $divId);\n$sql->execute();\n$sql->bind_result($text);\n$sql->fetch();\n$sql->close();\n$conn->close();\n\n// Output the text\necho $text;\n?>