<?php\nerror_reporting(E_ALL);\nini_set('display_errors', 1);\n\n$connection_string = "host=localhost port=5432 dbname=mydb user=myuser password=mypassword connect_timeout=5 options='--client_encoding=UTF8'";\n$db_crm = pg_connect($connection_string);\n\nif (!$db_crm) {\n    die("Connection failed: " . pg_last_error());\n} else {\n    echo "Connection successful!";\n}\n\n// Close the connection\npg_close($db_crm);\n?>