function convertToTimestamp($mysqlDate) {\n    list($year, $month, $day) = explode('-', $mysqlDate);\n    $date = new DateTime("$year-$month-$day", new DateTimeZone('UTC'));\n    return $date->getTimestamp();\n}\n\n// Example usage\n$dateEnrolled = convertToTimestamp('2008-08-26');\n$dateCompleted = -1000; // Magic value for not completed\n$resultDate = convertToTimestamp('2008-10-11');\n$certDate = convertToTimestamp('2008-10-10');