<?php\n   // Include required library for PayPal and Twitter APIs\n   require 'vendor/autoload.php';\n   \n   use Abraham\TwitterOAuth\TwitterOAuth;\n\n   // PayPal webhook listener\n   $raw_post_data = file_get_contents('php://input');\n   $decoded = json_decode($raw_post_data, true);\n\n   // Verify the webhook signature (this part can depend on your specific PayPal library)\n   // ...\n\n   // Check event type\n   if ($decoded['event_type'] === 'PAYMENT.SALE.COMPLETED') {\n       // Payment details\n       $payment_id = $decoded['resource']['id'];\n       $payer_email = $decoded['resource']['payer']['payer_info']['email'];\n\n       // Post a tweet\n       postTweet("Payment received from $payer_email. Payment ID: $payment_id");\n   }\n\n   function postTweet($message) {\n       $consumerKey = 'your-consumer-key';\n       $consumerSecret = 'your-consumer-secret';\n       $accessToken = 'your-access-token';\n       $accessTokenSecret = 'your-access-token-secret';\n\n       $connection = new TwitterOAuth($consumerKey, $consumerSecret, $accessToken, $accessTokenSecret);\n       $status = $connection->post("statuses/update", ["status" => $message]);\n\n       if ($connection->getLastHttpCode() == 200) {\n           echo 'Tweet posted successfully.';\n       } else {\n           echo 'Error posting tweet.';\n       }\n   }\n   ?>