class TableA extends Zend_Db_Table_Abstract\n{\n    protected $_schema = 'schema1';\n    protected $_name = 'tablea';\n    \n    protected $_dependentTables = array('TableB');\n    \n    public function getReference($table, $rule = null)\n    {\n        $reference = parent::getReference($table, $rule);\n        $reference['refTableClass'] = 'TableB'; // Specify the correct class name\n        $reference['refSchema'] = 'schema2';    // Specify the correct schema\n        return $reference;\n    }\n}\n\nclass TableB extends Zend_Db_Table_Abstract\n{\n    protected $_schema = 'schema2';\n    protected $_name = 'tableb';\n}