$playlistId = $_GET['playlistId']; // Assume the playlist ID is passed as a query parameter\n\n$sql = "SELECT xml_data FROM playlists WHERE id='$playlistId'";\n$result = $conn->query($sql);\n\nif ($result->num_rows > 0) {\n    // Output the XML data\n    while($row = $result->fetch_assoc()) {\n        header('Content-Type: application/xml');\n        echo $row["xml_data"];\n    }\n} else {\n    echo "No playlist found";\n}\n\n$conn->close();