class Bib {\n    protected $bibid;\n    public $isValid = true;\n\n    public function __construct($new_bibid) {\n        $this->bibid = $this->validate_bibid($new_bibid);\n        if ($this->bibid === false) {\n            $this->isValid = false;\n            echo 'me';\n        }\n        // Carry on with other stuff if valid bibid\n    }\n\n    private static function validate_bibid($test_bibid) {\n        // Call this method every time you get a bibid from the user\n        if (!is_int($test_bibid) && !ctype_digit($test_bibid)) {\n            return false;\n        }\n        return (int)$test_bibid;\n    }\n}\n\n// Usage\n$bib = new Bib('612e436');\nif (!$bib->isValid) {\n    echo 'Error Message';\n} else {\n    // Do what you intend to do with the object\n}