<?php\n$mysqli = new mysqli("your_host", "your_user", "your_password", "your_database");\n\n// Insert new download record\n$filename = 'file1.zip';\n$stmt = $mysqli->prepare("INSERT INTO downloads (filename) VALUES (?)");\n$stmt->bind_param("s", $filename);\n$stmt->execute();\n\n// Check the number of rows and delete the oldest one if there are more than 10\n$result = $mysqli->query("SELECT COUNT(*) AS count FROM downloads");\n$row = $result->fetch_assoc();\nif ($row['count'] > 10) {\n    $mysqli->query("DELETE FROM downloads ORDER BY download_date ASC LIMIT 1");\n}\n\n$stmt->close();\n$mysqli->close();\n?>