<?php\nheader("Content-Type: text/html; charset=utf-8");\n\nif (!isset($_GET["cmd"])) {\n    die("You should have a 'cmd' parameter in your URL");\n}\n\n$cmd = $_GET["cmd"];\n$pk = $_GET["pk"];\n\n$con = mysql_connect("localhost", "someuser", "notreal");\n\nif (!$con) {\n    die('Connection failed because of ' . mysql_error());\n}\n\nmysql_query('SET NAMES utf8');\nmysql_select_db("somedb", $con);\n\nif ($cmd == "GetAuctionData") {\n    $sql = "SELECT * FROM AUCTIONS WHERE ARTICLE_NO ='$pk'";\n    $sql2 = "SELECT ARTICLE_DESC FROM AUCTIONS WHERE ARTICLE_NO ='$pk'";\n    \n    $htmlset = mysql_query($sql2);\n    $row2 = mysql_fetch_array($htmlset);\n\n    $result = mysql_query($sql);\n\n    function savePicture($imageUrl, $pk) {\n        $ch = curl_init();\n        curl_setopt($ch, CURLOPT_URL, $imageUrl);\n        curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);\n        curl_setopt($ch, CURLOPT_CONNECTTIMEOUT, 0);\n        $fileContents = curl_exec($ch);\n        \n        if ($fileContents === false) {\n            echo 'Curl error: ' . curl_error($ch);\n        }\n\n        curl_close($ch);\n\n        if ($fileContents) {\n            $newImg = imagecreatefromstring($fileContents);\n            if ($newImg === false) {\n                echo 'Image creation failed';\n                return false;\n            }\n            return imagejpeg($newImg, "./{$pk}.jpg", 100);\n        }\n        return false;\n    }\n\n    while ($row = mysql_fetch_array($result)) {\n        $lastImg = $row['PIC_URL'];\n        savePicture($lastImg, $pk);\n        echo "\n        <div id='rightlayer'>\n            <img src='./".$pk.".jpg' width='100' height='100'>\n        </div>";\n    }\n\n    mysql_free_result($result);\n}\n\nmysql_close($con);\n?>