<?php\nfunction measureExecutionTime($callback)\n{\n    $start = microtime(true);\n    $callback();\n    $end = microtime(true);\n    return $end - $start;\n}\n\n$a = '/';\n$b = '/a/very/long/path/to/check/';\n\n$isDirTimeA = measureExecutionTime(function() use ($a) {\n    is_dir($a);\n});\n\n$isFileTimeA = measureExecutionTime(function() use ($a) {\n    is_file($a);\n});\n\n$isDirTimeB = measureExecutionTime(function() use ($b) {\n    is_dir($b);\n});\n\n$isFileTimeB = measureExecutionTime(function() use ($b) {\n    is_file($b);\n});\n\necho "is_dir(\$a) execution time: $isDirTimeA seconds\n";\necho "is_file(\$a) execution time: $isFileTimeA seconds\n";\necho "is_dir(\$b) execution time: $isDirTimeB seconds\n";\necho "is_file(\$b) execution time: $isFileTimeB seconds\n";\n?>