<?php\nclass Main {\n    public $vars = array();\n}\n\n$main = new Main;\n$main->vars['key'] = 'value';\n\nclass Extended extends Main {\n    public function __construct() {\n        // Inheriting properties from Main class\n        parent::__construct();\n    }\n}\n\n// Instantiate the Extended class\n$other = new Extended();\n\n// Access the inherited vars property\nvar_dump($other->vars);\n?>