function getValueByString($array, $string) {\n    // Extract keys from the string\n    preg_match_all('/\["([^"]*)"\]/', $string, $matches);\n    $keys = $matches[1];\n\n    // Traverse the array using the extracted keys\n    $value = $array;\n    foreach ($keys as $key) {\n        if (isset($value[$key])) {\n            $value = $value[$key];\n        } else {\n            return null; // Key does not exist\n        }\n    }\n    return $value;\n}\n\n// Example usage\n$data = [\n    "status" => "ok",\n    "response" => ["url" => "http://example.com"],\n    "entry" => [["text" => "sample text"]]\n];\n\n$strings = [\n    'data["status"]',\n    'data["response"]["url"]',\n    'data["entry"]["0"]["text"]'\n];\n\nforeach ($strings as $string) {\n    echo getValueByString($data, $string) . "\n";\n}