<?php\n   // Check if the Imagick extension is loaded\n   if (!extension_loaded('imagick')) {\n       echo 'Imagick extension is not loaded.';\n       exit;\n   }\n\n   // Path to the PDF file\n   $pdfPath = 'path/to/your/document.pdf';\n\n   // Create a new Imagick object\n   $imagick = new Imagick();\n\n   try {\n       // Read the PDF file\n       $imagick->readImage($pdfPath . '[0]'); // [0] indicates the first page\n\n       // Set the image format to PNG\n       $imagick->setImageFormat('png');\n\n       // Set the resolution (optional)\n       $imagick->setResolution(300, 300);\n\n       // Write the image to a file\n       $imagePath = 'path/to/output/preview.png';\n       $imagick->writeImage($imagePath);\n\n       echo 'PDF converted to image successfully: ' . $imagePath;\n   } catch (ImagickException $e) {\n       echo 'Error: ' . $e->getMessage();\n   } finally {\n       // Clear the Imagick object\n       $imagick->clear();\n       $imagick->destroy();\n   }\n   ?>