class UserTable extends Zend_Db_Table_Abstract {\n    protected $_name = 'users';\n\n    // Custom method for fetching active users\n    public function fetchActiveUsers() {\n        $select = $this->select()->where('status = ?', 'active');\n        return $this->fetchAll($select);\n    }\n}\n\n// Usage in a controller\n$userTable = new UserTable();\n$activeUsers = $userTable->fetchActiveUsers();