// Sample function to get user access level and location\n   function getUserAccess($userId, $pdo) {\n       $stmt = $pdo->prepare('SELECT r.access_level, u.location FROM users u JOIN roles r ON u.role_id = r.role_id WHERE u.user_id = ?');\n       $stmt->execute([$userId]);\n       return $stmt->fetch(PDO::FETCH_ASSOC);\n   }\n\n   // Sample function to fetch records based on user access\n   function fetchRecordsBasedOnAccess($userId, $pdo) {\n       $accessInfo = getUserAccess($userId, $pdo);\n       $accessLevel = $accessInfo['access_level'];\n       $location = $accessInfo['location'];\n\n       if ($accessLevel == 'national') {\n           $stmt = $pdo->prepare('SELECT * FROM records');\n       } elseif ($accessLevel == 'city') {\n           $stmt = $pdo->prepare('SELECT * FROM records WHERE city = ?');\n           $stmt->execute([$location]);\n       } else {\n           // Handle other access levels if necessary\n           return [];\n       }\n\n       return $stmt->fetchAll(PDO::FETCH_ASSOC);\n   }\n\n   // Usage\n   $userId = $_SESSION['user_id']; // Assuming user ID is stored in session\n   $records = fetchRecordsBasedOnAccess($userId, $pdo);