<?php\n// Use prepared statements to prevent SQL injection\n$mysqli = new mysqli("localhost", "username", "password", "database");\n\n// Check connection\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n// Prepare the SQL statement\n$query = $mysqli->prepare("SELECT news.*, users.username FROM news JOIN users ON news.user_id = users.id WHERE news.id = ?");\n$query->bind_param("i", $_GET['id']); // Bind the GET parameter to the query\n\n// Execute the query\n$query->execute();\n\n// Get the result\n$result = $query->get_result();\n\n// Fetch the data\nif ($news = $result->fetch_assoc()) {\n    echo "News Title: " . $news['title'] . "<br>";\n    echo "Author: " . $news['username'] . "<br>";\n    // other fields...\n}\n\n// Close the statement and connection\n$query->close();\n$mysqli->close();\n?>