// Insert the reply into tblpm\nmysqli_query($conn, "INSERT INTO `tblpm` (title, content, sender_id, date_sent) VALUES ('$reply_subject', '$reply_message', '$sender', NOW())");\n\n// Get the auto-incremented ID of the newly inserted reply\n$reply_message_id = mysqli_insert_id($conn);\n\n// Insert the reply info into tblpm_info for each recipient\nforeach ($recipients as $recipient_id) {\n    mysqli_query($conn, "INSERT INTO `tblpm_info` (message_id, sender_id, receiver_id) VALUES ('$reply_message_id', '$sender', '$recipient_id')");\n}