// Disable WSDL caching\nini_set('soap.wsdl_cache_enabled', '0');\nini_set('soap.wsdl_cache_ttl', '0');\n\n// Path to the temporary directory where WSDL cache files are stored\n$tempDir = sys_get_temp_dir();\n\n// Pattern to match WSDL cache files\n$pattern = $tempDir . '/wsdl-*';\n\n// Find and delete all WSDL cache files\nforeach (glob($pattern) as $file) {\n    unlink($file);\n}\n\n// Re-enable WSDL caching if needed\nini_set('soap.wsdl_cache_enabled', '1');\nini_set('soap.wsdl_cache_ttl', '86400'); // Example: cache TTL of 1 day\n\n// Now you can use your SOAP client and it will fetch the new WSDL\n$client = new SoapClient('path_to_your_wsdl.wsdl');