<?php\nuse PHPMailer\PHPMailer\PHPMailer;\nuse PHPMailer\PHPMailer\Exception;\n\nrequire 'vendor/autoload.php';\n\n// Database connection\n$mysqli = new mysqli("host", "user", "password", "database");\n\n// Check connection\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n// Fetch HTML content from the database\n$sql = "SELECT html_content FROM your_table WHERE id = 1";\n$result = $mysqli->query($sql);\n\nif ($result->num_rows > 0) {\n    $row = $result->fetch_assoc();\n    $htmlContent = $row['html_content'];\n} else {\n    die("No HTML content found in the database.");\n}\n\n// Create a new PHPMailer instance\n$mail = new PHPMailer(true);\n\ntry {\n    //Server settings\n    $mail->isSMTP();\n    $mail->Host = 'smtp.example.com';\n    $mail->SMTPAuth = true;\n    $mail->Username = 'your-email@example.com';\n    $mail->Password = 'your-email-password';\n    $mail->SMTPSecure = 'tls'; \n    $mail->Port = 587;\n\n    //Recipients\n    $mail->setFrom('from@example.com', 'Mailer');\n    $mail->addAddress('recipient@example.com', 'Recipient Name');\n\n    // Attach the HTML content as a file\n    $mail->addStringAttachment($htmlContent, 'attachment.html');\n\n    // Content\n    $mail->isHTML(true);\n    $mail->Subject = 'Here is your HTML file';\n    $mail->Body    = 'Please find the attached HTML file.';\n    $mail->AltBody = 'Please find the attached HTML file.';\n\n    $mail->send();\n    echo 'Message has been sent';\n} catch (Exception $e) {\n    echo "Message could not be sent. Mailer Error: {$mail->ErrorInfo}";\n}\n\n// Close the database connection\n$mysqli->close();\n?>