// Create a custom module in Joomla (mod_unified_login)\n\ndefined('_JEXEC') or die;\n\n// Load necessary Joomla classes\njimport('joomla.application.module.helper');\njimport('joomla.user.authentication');\n\n// Start the session\nJFactory::getSession()->start();\n\n// Check if form is submitted\nif ($_SERVER['REQUEST_METHOD'] === 'POST') {\n    $username = $_POST['username'];\n    $password = $_POST['password'];\n\n    // Get the Joomla authentication object\n    $auth = JAuthentication::getInstance();\n    $credentials = array('username' => $username, 'password' => $password);\n    $response = $auth->authenticate($credentials, array());\n\n    if ($response->status === JAuthentication::STATUS_SUCCESS) {\n        // Get the user object\n        $user = JUser::getInstance($response->username);\n        // Log the user in\n        $app = JFactory::getApplication();\n        $app->login(array('username' => $username, 'password' => $password));\n\n        // Check if user is in the admin group\n        $isAdmin = $user->authorise('core.login.admin');\n\n        if ($isAdmin) {\n            // Redirect to the admin dashboard\n            $app->redirect(JRoute::_('administrator/index.php'));\n        } else {\n            // Redirect to the user dashboard\n            $app->redirect(JRoute::_('index.php'));\n        }\n    } else {\n        echo 'Login failed. Please check your username and password.';\n    }\n}\n\n// Display the login form\n?>\n\n<form action="" method="post">\n    <label for="username">Username:</label>\n    <input type="text" name="username" id="username" required>\n    <br>\n    <label for="password">Password:</label>\n    <input type="password" name="password" id="password" required>\n    <br>\n    <button type="submit">Login</button>\n</form>