<?php\n// Command to get the system load\n$command = 'typeperf "\Processor(_Total)\% Processor Time" -sc 1';\n\n// Execute the command and capture the output\n$output = shell_exec($command);\n\n// Extract the load value from the output\npreg_match('/"[^"]*","([^"]*)"/', $output, $matches);\n\nif (isset($matches[1])) {\n    // Convert the load to a float\n    $load = floatval($matches[1]);\n    echo "System Load: $load%\n";\n\n    // Define your threshold\n    $threshold = 70.0;\n\n    // Check if the load is below the threshold\n    if ($load < $threshold) {\n        echo "System load is below threshold. Proceeding with the task...\n";\n        // Your image processing script logic here\n    } else {\n        echo "System load is too high. Skipping the task...\n";\n    }\n} else {\n    echo "Failed to retrieve system load.\n";\n}\n?>