$var = 'test';\n\n$iterations = 100000;\n$start = microtime(true);\nfor ($i = 0; $i < $iterations; $i++) {\n    if ($var != 'test1' && $var != 'test2' && $var != 'test3' && $var != 'test4') {\n        // Do something\n    }\n}\n$end = microtime(true);\necho "Multiple expressions: " . ($end - $start) . " seconds\n";\n\n$start = microtime(true);\nfor ($i = 0; $i < $iterations; $i++) {\n    if (!in_array($var, array('test1', 'test2', 'test3', 'test4'))) {\n        // Do something\n    }\n}\n$end = microtime(true);\necho "in_array: " . ($end - $start) . " seconds\n";