$conn_id = ftp_connect('your_ftp_server');\n$login_result = ftp_login($conn_id, 'username', 'password');\n\nif ($login_result) {\n    if (ftp_rename($conn_id, $old_file, $new_file)) {\n        echo "successfully renamed $old_file to $new_file\n";\n    } else {\n        $error_message = ftp_raw($conn_id, "STAT");\n        echo "There was a problem while renaming $old_file to $new_file: the server says " . implode("\n", $error_message) . "\n";\n    }\n} else {\n    echo "Couldn't connect or login to FTP server\n";\n}\n\nftp_close($conn_id);