<?php\n   // Create a TCP/IP socket\n   $socket = socket_create(AF_INET, SOCK_STREAM, SOL_TCP);\n   if ($socket === false) {\n       echo "Socket creation failed: " . socket_strerror(socket_last_error()) . "\n";\n   }\n\n   // Bind the socket to an address and port\n   $result = socket_bind($socket, '127.0.0.1', 8080);\n   if ($result === false) {\n       echo "Socket bind failed: " . socket_strerror(socket_last_error($socket)) . "\n";\n   }\n\n   // Listen for incoming connections\n   $result = socket_listen($socket, 5);\n   if ($result === false) {\n       echo "Socket listen failed: " . socket_strerror(socket_last_error($socket)) . "\n";\n   }\n\n   do {\n       $client_socket = socket_accept($socket);\n       if ($client_socket === false) {\n           echo "Socket accept failed: " . socket_strerror(socket_last_error($socket)) . "\n";\n           break;\n       }\n\n       // Read client message\n       $input = socket_read($client_socket, 1024);\n       echo "Received message: $input\n";\n\n       // Send response to client\n       $response = "Hello Client!";\n       socket_write($client_socket, $response, strlen($response));\n\n       // Close the client socket\n       socket_close($client_socket);\n   } while (true);\n\n   // Close the server socket\n   socket_close($socket);\n   ?>