<?php\n$url = $_SERVER['argv'][0]; // e.g., page=6&test=1&test2=2\n\n// Use regex to remove the 'page' parameter, considering it might not be the first or last parameter\n$url_without_page_var = preg_replace('/([&?])page=\d+(&|$)/i', '$1', $url);\n\n// Remove any trailing '&' or '?' that might be left over\n$url_without_page_var = rtrim($url_without_page_var, '&');\n$url_without_page_var = rtrim($url_without_page_var, '?');\n\n// If the URL starts with '&', remove it\nif (strpos($url_without_page_var, '&') === 0) {\n    $url_without_page_var = substr($url_without_page_var, 1);\n}\n\necho $url_without_page_var; // Outputs: test=1&test2=2\n?>