$host = 'localhost';\n$user = 'your_username';\n$password = 'your_password';\n$dbname = 'your_database';\n$backup_file = 'backup.sql';\n\n$command = "mysqldump --opt --host={$host} --user={$user} --password={$password} {$dbname} > {$backup_file}";\nexec($command, $output, $return_var);\n\nif ($return_var === 0) {\n    echo "Backup successful!";\n} else {\n    echo "Backup failed!";\n}