<?php\n// Path to your C++ binary\n$binary = '/path/to/your/cplusplus_binary';\n\n// Capture POST data if needed\n$post_data = file_get_contents('php://input');\n\n// Execute the C++ binary\n$descriptorspec = array(\n    0 => array("pipe", "r"),  // stdin\n    1 => array("pipe", "w"),  // stdout\n    2 => array("pipe", "w")   // stderr\n);\n\n$process = proc_open($binary, $descriptorspec, $pipes);\n\nif (is_resource($process)) {\n    if ($post_data) {\n        fwrite($pipes[0], $post_data);\n    }\n    fclose($pipes[0]);\n\n    $output = stream_get_contents($pipes[1]);\n    fclose($pipes[1]);\n\n    // Capture stderr if needed\n    $error_output = stream_get_contents($pipes[2]);\n    fclose($pipes[2]);\n\n    $return_value = proc_close($process);\n\n    // Print the output to the web browser\n    header('Content-Type: text/html');\n    echo $output;\n}\n?>