<?php\n$domain = '_xmpp-server._tcp.gmail.com';\n$records = dns_get_record($domain, DNS_SRV);\n\nif ($records === false) {\n    echo "DNS lookup failed.";\n} else {\n    foreach ($records as $record) {\n        echo "Priority: " . $record['pri'] . "\n";\n        echo "Weight: " . $record['weight'] . "\n";\n        echo "Port: " . $record['port'] . "\n";\n        echo "Target: " . $record['target'] . "\n\n";\n    }\n}\n?>