App::import('Component', 'Email');\nclass MyModel extends AppModel {\n    function sendErrorEmail($to, $from, $subject) {\n        // Instantiate the EmailComponent\n        $email = new EmailComponent(null);\n\n        // Configure the email settings\n        $email->initialize($this);\n        $email->delivery = 'smtp';\n        $email->template = 'default';\n        $email->sendAs = 'text';\n        $email->from    = $from;\n        $email->to      = $to;\n        $email->subject = $subject;\n\n        // Send the email\n        if ($email->send()) {\n            return true;\n        } else {\n            return false;\n        }\n    }\n}