<?php\nrequire_once 'vendor/autoload.php'; // Ensure you have installed the Facebook PHP SDK via Composer\n\nuse Facebook\Facebook;\n\n$appId = 'your-app-id';\n$appSecret = 'your-app-secret';\n$accessToken = 'your-user-access-token'; // Make sure this token has the required permissions\n\n$fb = new Facebook([\n  'app_id' => $appId,\n  'app_secret' => $appSecret,\n  'default_graph_version' => 'v12.0',\n]);\n\n$message = "Will this status show up and allow me to dominate the world?!";\n\ntry {\n  // Post to the user's timeline\n  $response = $fb->post('/me/feed', ['message' => $message], $accessToken);\n  echo 'Post ID: ' . $response->getGraphNode()['id'];\n} catch(Facebook\Exceptions\FacebookResponseException $e) {\n  // When Graph returns an error\n  echo 'Graph returned an error: ' . $e->getMessage();\n  exit;\n} catch(Facebook\Exceptions\FacebookSDKException $e) {\n  // When validation fails or other local issues\n  echo 'Facebook SDK returned an error: ' . $e->getMessage();\n  exit;\n}\n?>