<?php\n// Instead of relying on mbstring.func_overload, use the multi-byte functions directly\n$unicodeString = "こんにちは";\n\n// Use mb_strlen() instead of strlen()\n$length = mb_strlen($unicodeString, 'UTF-8');\necho "Length of the string: $length\n";\n\n// Use mb_strpos() instead of strpos()\n$position = mb_strpos($unicodeString, "に", 0, 'UTF-8');\necho "Position of 'に': $position\n";\n\n// Use mb_substr() instead of substr()\n$substring = mb_substr($unicodeString, 2, 3, 'UTF-8');\necho "Substring: $substring\n";\n?>