<?php\n$text = "Your document text here with <p>paragraphs</p> and <p>more paragraphs</p>.";\n\n// Regular expression to match content between <p> and </p>\n$pattern = '/<p>(.*?)<\/p>/s';\n\n// Find all matches\npreg_match_all($pattern, $text, $matches);\n\n// Check if any matches were found\nif (!empty($matches[1])) {\n    // Shuffle the matches\n    shuffle($matches[1]);\n    \n    // Pick the first match after shuffling\n    $randomParagraph = $matches[1][0];\n    \n    // Output the randomly picked paragraph\n    echo $randomParagraph;\n} else {\n    echo "No matches found.";\n}\n?>