use Drupal\views\Views;\n\n// Load the view by its machine name and display ID.\n$view = Views::getView('your_view_machine_name');\n$view->setDisplay('your_display_id');\n\n// Execute the view to get the results.\n$view->execute();\n\n// Access the results as an array.\n$results = $view->result;\n\n// Example: Iterate over the results and print the fields.\nforeach ($results as $row) {\n  // Access individual fields in the row.\n  $field_data = $row->_entity->get('field_name')->value;\n  // Do something with each field.\n  // For example, print the field value.\n  print $field_data;\n}