<?php\n// SSH server details\n$sshHost = 'SSH_SERVER_IP';\n$sshPort = 22;\n$sshUser = 'SSH_USERNAME';\n$sshPassword = 'SSH_PASSWORD';\n\n// MySQL server details\n$dbHost = '127.0.0.1';\n$dbPort = 3307; // local port forwarded to MySQL server\n$dbUser = 'DB_USERNAME';\n$dbPassword = 'DB_PASSWORD';\n$dbName = 'DB_NAME';\n\n// Establish SSH tunnel using system command\n$tunnelCommand = "sshpass -p $sshPassword ssh -N -L $dbPort:localhost:3306 $sshUser@$sshHost -p $sshPort > /dev/null 2>&1 &";\nexec($tunnelCommand);\n\n// Wait a moment for the tunnel to establish\nsleep(2);\n\n// Connect to MySQL through the established tunnel\n$mysqli = new mysqli($dbHost, $dbUser, $dbPassword, $dbName, $dbPort);\n\nif ($mysqli->connect_error) {\n    die('Connect Error (' . $mysqli->connect_errno . ') ' . $mysqli->connect_error);\n}\n\necho 'Success: A proper connection to MySQL was made!';\n\n// Use the database connection...\n\n// Close the MySQL connection\n$mysqli->close();\n?>