<?php\n// Set up the context with the HTTP headers, including the cookies\n$opts = array(\n    'http' => array(\n        'header' => 'Cookie: ' . $_SERVER['HTTP_COOKIE'] . "\r\n"\n    )\n);\n\n// Create the context resource\n$context = stream_context_create($opts);\n\n// Use the context while fetching the contents\n$contents = file_get_contents("http://www.example.com/test1.php", false, $context);\n\nif ($contents === FALSE) {\n    // Handle error\n    echo "Error: Unable to fetch content.";\n} else {\n    // Process the contents\n    echo $contents;\n}\n?>