<?php\n$selfurl = "http://" . $_SERVER['SERVER_NAME'] . dirname($_SERVER['PHP_SELF']) . "/";\n\n// Parse the query string into an associative array\nparse_str($_SERVER['QUERY_STRING'], $queryParams);\n\n// Remove the 'page' parameter if it exists\nunset($queryParams['page']);\n\n// Convert the query parameters back to a query string\n$queryString = http_build_query($queryParams);\n\n// Calculate the next and previous page numbers\n$current_page = isset($_GET['page']) ? (int)$_GET['page'] : 1;\n$next_page = $current_page + 1;\n$previous_page = max(1, $current_page - 1);\n\n// Generate the pagination links\n$next_link = $selfurl . "?" . $queryString . "&page=" . $next_page;\n$previous_link = $selfurl . "?" . $queryString . "&page=" . $previous_page;\n?>\n\n<a href="<?php echo htmlspecialchars($next_link); ?>" class="bluelinkbold">Next</a>\n<a href="<?php echo htmlspecialchars($previous_link); ?>" class="bluelinkbold">Previous</a>