<?php\n// Open the CSV file\n$file = fopen("files/data.csv", "r");\nif ($file === false) {\n    die("Error opening the file.");\n}\n\n// Initialize an array to hold the data\n$data = [];\n\n// Loop through each line in the file\nwhile (($line = fgetcsv($file, 0, ";")) !== false) {\n    $data[] = $line;\n}\n\n// Close the file\nfclose($file);\n\n// Output the data (for debugging purposes)\necho "<pre>";\nprint_r($data);\necho "</pre>";\n?>