$key = 'your-encryption-key'; // Use a securely generated key\n\n// Data to be encrypted\n$pet_id = 123;\n$variation_id = 456;\n$data = json_encode(['pet_id' => $pet_id, 'variation_id' => $variation_id]);\n\n// Encrypt\n$encrypted_data = encrypt($data, $key);\necho "Encrypted URL data: " . urlencode($encrypted_data) . "\n";\n\n// Decrypt\n$decrypted_data = decrypt(urldecode($encrypted_data), $key);\n$decoded_data = json_decode($decrypted_data, true);\n\necho "Decrypted pet ID: " . $decoded_data['pet_id'] . "\n";\necho "Decrypted variation ID: " . $decoded_data['variation_id'] . "\n";