function extractFirstImageSrc($htmlContent) {\n    // Suppress errors due to malformed HTML\n    libxml_use_internal_errors(true);\n\n    // Create a new DOMDocument and load the HTML content\n    $dom = new DOMDocument();\n    $dom->loadHTML($htmlContent);\n\n    // Find the first <img> tag\n    $images = $dom->getElementsByTagName('img');\n    if ($images->length > 0) {\n        // Get the 'src' attribute of the first <img> tag\n        $firstImage = $images->item(0);\n        $src = $firstImage->getAttribute('src');\n        return $src;\n    }\n\n    // Return null if no <img> tags are found\n    return null;\n}\n\n// Example usage\n$htmlContent = '<html><body><img src="example.jpg" alt="example"><img src="another.jpg"></body></html>';\n$src = extractFirstImageSrc($htmlContent);\necho $src;  // Outputs: example.jpg