<?php\ndefined('BASEPATH') OR exit('No direct script access allowed');\n\nclass Xmlrpc_server extends CI_Controller {\n\n    public function __construct() {\n        parent::__construct();\n        $this->load->library('xmlrpc');\n        $this->load->library('xmlrpcs');\n    }\n\n    public function index() {\n        $config['functions']['weblogUpdates.ping'] = array('function' => 'Xmlrpc_server.weblogUpdates_ping');\n        $config['functions']['ping'] = array('function' => 'Xmlrpc_server.weblogUpdates_ping');\n        $config['functions']['weblogUpdates'] = array('function' => 'Xmlrpc_server.weblogUpdates_ping');\n        $config['object'] = $this;\n        $this->xmlrpcs->initialize($config);\n        $this->xmlrpcs->serve();\n    }\n\n    public function weblogUpdates_ping($request) {\n        // Extract parameters from request\n        $parameters = $request->output_parameters();\n        $blogName = $parameters[0];\n        $blogUrl = $parameters[1];\n\n        // Process the ping request (you can add your logic here)\n        // Example response\n        $response = array(\n            array(\n                'flerror' => false,\n                'message' => "Ping received from: $blogName at $blogUrl"\n            ),\n            'struct'\n        );\n\n        return $this->xmlrpc->send_response($response);\n    }\n}\n?>