<?php\nfunction replaceWord($string, $search, $replace) {\n    // Use a regular expression to find the word that contains the search term\n    $pattern = '/\b\w*' . preg_quote($search, '/') . '\w*\b/i';\n    \n    // Replace the found word with the replacement word\n    $result = preg_replace($pattern, $replace, $string);\n    \n    return $result;\n}\n\n// Example usage\n$string1 = "lorem ipsum";\n$string2 = "loreal ipsum";\n\necho replaceWord($string1, "lo", "lorem can"); // Outputs: lorem can ipsum\necho "\n";\necho replaceWord($string2, "lo", "loreal can"); // Outputs: loreal can ipsum\n?>