require('libs/Smarty.class.php');\n\n$smarty = new Smarty;\n\n// Logic to determine the list of template directories based on the URL\n$url = $_SERVER['REQUEST_URI'];\n\nif (strpos($url, '/some/path') !== false) {\n    $template_dirs = array(\n        '/path/to/first/folder',\n        '/path/to/second/folder'\n    );\n} else {\n    $template_dirs = array(\n        '/path/to/third/folder',\n        '/path/to/fourth/folder'\n    );\n}\n\n// Assign the array to the template_dir property\n$smarty->setTemplateDir($template_dirs);\n\n// Display a template\n$smarty->display('example.tpl');