<?php\n// Define the paths to the input PDF and output TXT files\n$inputPdf = 'file.pdf';\n$outputTxt = 'file.txt';\n\n// Ensure the input PDF file exists\nif (!file_exists($inputPdf)) {\n    die("Input PDF file does not exist.");\n}\n\n// Execute the pdftotext command and capture any errors\n$command = escapeshellcmd("pdftotext $inputPdf $outputTxt 2>&1");\n$output = [];\n$returnVar = 0;\nexec($command, $output, $returnVar);\n\n// Check for errors\nif ($returnVar !== 0) {\n    echo "Error executing pdftotext:\n";\n    echo implode("\n", $output);\n} else {\n    echo "PDF successfully converted to TXT.";\n}\n?>