<?php\n// Assuming this is the value you want to retain in the form\n$value = isset($_POST['something']) ? $_POST['something'] : '';\n\n// Use htmlspecialchars to escape special characters\n$escaped_value = htmlspecialchars($value, ENT_QUOTES, 'UTF-8');\n?>\n\n<form method="post" action="">\n    <input type="text" name="something" value="<?php echo $escaped_value; ?>" />\n    <input type="submit" value="Submit" />\n</form>