function updateDemo($demoTitle, $desc, $keyword, $uploadedFile, $clientname, $uploadedImage, $adminName, $demoID) {\n    // Database connection\n    $mysqli = new mysqli("localhost", "username", "password", "database");\n\n    // Check connection\n    if ($mysqli->connect_error) {\n        die("Connection failed: " . $mysqli->connect_error);\n    }\n\n    // Prepare the SQL statement\n    $stmt = $mysqli->prepare("UPDATE demos SET dmTitle = ?, dmDesc = ?, dmKey = ?, dmLink = ?, client = ?, imageTitle = ?, userName = ? WHERE id = ?");\n    \n    if ($stmt === false) {\n        die("Prepare failed: " . $mysqli->error);\n    }\n\n    // Bind parameters\n    $stmt->bind_param("sssssssi", $demoTitle, $desc, $keyword, $uploadedFile, $clientname, $uploadedImage, $adminName, $demoID);\n\n    // Execute the statement\n    if ($stmt->execute()) {\n        $result = 'yes';\n    } else {\n        $result = $stmt->error;\n    }\n\n    // Close the statement and connection\n    $stmt->close();\n    $mysqli->close();\n\n    return $result;\n}