// Original function\nfunction myfunction() {\n    echo "Test";\n}\n\n// Store the eval code in a variable\n$evalCode = '\n  function myevalfunction() {\n    echo "Test";\n  }\n';\n\n// Evaluate the code\neval($evalCode);\n\n// Use Reflection to get information about the function\n$a = new ReflectionFunction('myfunction');\necho $a . PHP_EOL;\n\n$b = new ReflectionFunction('myevalfunction');\necho $b . PHP_EOL;\n\n// Output the stored eval code\necho "Source code of myevalfunction: " . PHP_EOL;\necho $evalCode;