// Model/DbTable/Person.php\n   class Model_DbTable_Person extends Zend_Db_Table_Abstract {\n       protected $_name = 'Person';\n   }\n\n   // Model/PersonMapper.php\n   class Model_PersonMapper {\n       protected $table;\n\n       public function __construct() {\n           $this->table = new Model_DbTable_Person();\n       }\n\n       public function fetchById($id) {\n           $row = $this->table->fetchRow('person_id = ' . (int)$id);\n           if (!$row) {\n               throw new Exception("Person not found");\n           }\n           return $this->mapRowToDomain($row);\n       }\n\n       protected function mapRowToDomain($row) {\n           switch ($row->userType) {\n               case 'admin':\n                   return new Admin($row->person_id, $row->userType);\n               case 'buyer':\n                   return new Buyer($row->person_id, $row->userType);\n               case 'associate':\n                   return new Associate($row->person_id, $row->userType);\n               case 'supervisor':\n                   return new Supervisor($row->person_id, $row->userType);\n               default:\n                   return new Person($row->person_id, $row->userType);\n           }\n       }\n   }